mapper.select:
SELECT noticenum, nickname, startdate, title, hits, creatorid, boardtype FROM t3_board where 1=1
and title like concat('%', #{title}, '%')
and boardtype = #{boardtype}
ORDER BY noticenum DESC LIMIT #{startNum}, #{lastNum}

SELECT COUNT(*) AS CNT FROM t3_board where 1=1
and title like concat('%', #{title}, '%')
and boardtype = #{boardtype}

mapper.update:

	UPDATE t3_board 
	SET
		title = #{title}, content = #{content} 
	WHERE noticenum = #{noticenum}

mapper.delete:

	DELETE FROM 
		t3_board 
	WHERE 
		noticenum IN ( 
			#{item.noticenum} 
		)
