mapper.select:

		SELECT 
			reviewnum, categori, resnum ,title, writedate, content, grade, nickname, create_id, resname
		FROM
			t3_review
		WHERE 1=1
		
		and (#{flg} or reskind = #{reskind}) 
		
		
		and resname = #{resname} 
		
		
		and categori = #{categori} 
		
		ORDER BY writedate DESC
		LIMIT #{startNum}, #{lastNum}

SELECT COUNT(*) AS CNT FROM t3_review WHERE 1=1
and (#{flg} or reskind = #{reskind})
and resname = #{resname}
and categori = #{categori}

SELECT reviewnum ,categori ,resnum ,title ,content ,writedate ,grade ,nickname ,answer FROM t3_review WHERE 1=1
and resnum = #{resnum}
ORDER BY writedate ASC LIMIT #{startNum}, #{lastNum}

mapper.insert:

	update  t3_review
	set answer = #{answer}  
	where
		reviewnum = #{reviewnum}
